Page._content:
/* Custom styles for code blocks and background */
body {
    background-color: var(--body-bg);
}

.content {
    background-color: var(--card-bg);
    padding: 2rem;
    border-radius: 10px;
    margin: 2rem auto;
    max-width: 800px;
}

/* Code block styling */
pre {
    background-color: #282c34 !important;
    border-radius: 8px;
    padding: 1rem;
    margin: 1rem 0;
    overflow-x: auto;
}

code {
    font-family: 'Fira Code', 'JetBrains Mono', monospace;
    font-size: 0.9em;
    background-color: transparent !important;
}

/* Syntax highlighting */
.highlight {
    background-color: #282c34 !important;
    border-radius: 8px;
    margin: 1rem 0;
}

.highlight pre {
    background-color: transparent !important;
    margin: 0;
    padding: 1rem;
}

/* Dark mode adjustments */
@media (prefers-color-scheme: dark) {
    .content {
        background-color: var(--card-bg);
    }

    pre,
    .highlight {
        background-color: #1e1e1e !important;
    }
}

